using UnityEngine;//using UnityEditor;using System.Collections;using System.Collections.Generic;public class Girl2D : MonoBehaviour{	public enum EAnimDir { EAD_Left = 1, EAD_Right, EAD_Up, EAD_Down };	public EAnimDir m_AnimDir = EAnimDir.EAD_Left;	public Material m_MatOutline;	public Color m_OutlineColor = Color.white;	[Range(1, 2)] public int m_OutlineSize = 1;		private Animator m_Animator;	private Material m_MatOrig;	private SpriteRenderer m_SprRdr;		void Start ()	{		m_Animator = this.GetComponent<Animator> ();		m_SprRdr = this.GetComponent<SpriteRenderer> ();		m_MatOrig = m_SprRdr.material;	}	void Update ()	{		if (m_AnimDir == EAnimDir.EAD_Left)			m_Animator.SetInteger ("Direction", 1);		else if (m_AnimDir == EAnimDir.EAD_Right)			m_Animator.SetInteger ("Direction", 2);		else if (m_AnimDir == EAnimDir.EAD_Up)			m_Animator.SetInteger ("Direction", 3);		else if (m_AnimDir == EAnimDir.EAD_Down)			m_Animator.SetInteger ("Direction", 4);	}	void OnMouseOver ()	{		m_SprRdr.material = m_MatOutline;		m_SprRdr.material.SetInt ("_OutlineSize", m_OutlineSize);		m_SprRdr.material.SetColor ("_OutlineColor", m_OutlineColor);	}	void OnMouseExit ()	{		m_SprRdr.material = m_MatOrig;	}}